import React, { useState, Fragment } from 'react'
import AddUserForm from './forms/AddUserForm'
import UserCard from './tables/UserCard'
import {Switch, Route, Link} from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css';



const App = () => {
	// Data
	const usersData = [
		{ id: Math.random(), name: 'Meftah Jaâfer', username: 'meftah_jaafer', image: 'https://bit.ly/3m9PrsI' },
		{ id: Math.random(), name: 'Elias Ben Azouz', username: 'elias_benazzouz', image: 'https://bit.ly/3m9PrsI' },
	]

	state = {
		items: usersData,
		id: Math.random(),
		name: '',
		username: '', 
		image: 'https://bit.ly/3m9PrsI',
		editItem:false
	  };
	
	// functions

	handleChange = e => {
		this.setState({
		  item:e.target.value
		});
	  };
	
	  handleSubmit = e => {
		e.preventDefault();
		const newItem = {
		  id: this.state.id,
		  name: this.state.name,
		  username: this.state.username,
		  image: this.state.image,
		};
		const updatedItems = [...this.state.items, newItem];
		this.setState({
		  items: updatedItems,
		  id: Math.random(),
		  name: '',
		  username: '', 
		  image: 'https://bit.ly/3m9PrsI',
		  editItem:false
		});
	  };
	

	
	  handleDelete = (id) => {
		const filteredItems = this.state.items.filter(item => item.id !== id);
		this.setState({
		  items: filteredItems
		});
	  };
	
	  handleEdit = (id) => {
		const filteredItems = this.state.items.filter(item => item.id !== id);
		const selectedItem = this.state.items.find(item => item.id === id)
		this.setState({
		  items: filteredItems,
		  id: id,
		  name: selectedItem.name,
		  username: selectedItem.username,
		  image: selectedItem.image,
		  editItem: true
		});
	  };


	return (

	<div class="container">
        <div class="row">
        <div class="col-10 mx-auto col-md-8 mt-4">
          <h3 className="text-capitalize text-center">CRUD APP</h3>
		  <ul className="list-group my-5">
            <h3 className="text-capitalize text-center">Users List</h3>
             {this.state.map(user => (
			   <UserCard key={user.id}
			       user={user}
			       handleDelete={() => handleDelete(user.id)}
			       handleEdit={() => handleEdit(user.id)}/>))}
			</ul>
           </div>
        </div>
	</div>
	)
}

export default App
