import React, { Component } from 'react';
import UserCard from './tables/UserCard'
import 'bootstrap/dist/css/bootstrap.min.css';
import AddEditUser from './forms/AddEditUser';



export default class App extends Component {


	state = {
		items: [],
		id: Math.random(),
		name: "",
		username: "", 
		image: "https://bit.ly/3m9PrsI'",
		editItem:false
		
	  };
	


	handleChange = e => {
		const value = e.target.value;
		this.setState({
			[e.target.name]: value
		});
	  };


	
	  handleSubmit = e => {
		e.preventDefault();
		const newItem = {
		  id: this.state.id,
		  name: this.state.name,
		  username: this.state.username,
		  image: this.state.image,
		};
		const updatedItems = [...this.state.items, newItem];
		this.setState({
		  items: updatedItems,
		  id: Math.random(),
		  name: "",
		  username: "", 
		  image: "https://bit.ly/3m9PrsI",
		  editItem:false
		});
	  };
	

	
	  handleDelete = (id) => {
		const filteredItems = this.state.items.filter(item => item.id !== id);
		this.setState({
		  items: filteredItems
		});
	  };
	
	  handleEdit = (id) => {
		const filteredItems = this.state.items.filter(item => item.id !== id);
		const selectedItem = this.state.items.find(item => item.id === id)
		this.setState({
		  items: filteredItems,
		  id: id,
		  name: selectedItem.name,
		  username: selectedItem.username,
		  image: selectedItem.image,
		  editItem: true
		});
	  };

render() {
	return (

	<div class="container">
        <div class="row">
        <div class="col-10 mx-auto col-md-8 mt-4">
          <h3 className="text-capitalize text-center">CRUD APP</h3>
          <h3 className="text-capitalize text-center">Todo Input</h3>
             <AddEditUser
             item={this.state}
             handleChange={this.handleChange}
             handleSubmit={this.handleSubmit}
             editItem={this.state.editItem}/>

		  <ul className="list-group my-5">
		  <h3 className="text-capitalize text-center">Users List</h3>
             {this.state.items.map(user => (
			   <UserCard key={user.id}
			       user={user}
			       handleDelete={() => this.handleDelete(user.id)}
			       handleEdit={() => this.handleEdit(user.id)}/>))}
			</ul>
           </div>
        </div>
	</div>
		)}
}
