import React, { useState, Fragment } from 'react'
import AddUserForm from './forms/AddUserForm'
import EditUserForm from './forms/EditUserForm'
import UserCard from './tables/UserCard'
import {Switch, Route, Link} from 'react-router-dom'



const App = () => {
	// Data
	const usersData = [
		{ id: Math.random(), name: 'Tania', username: 'floppydiskette', image: 'https://bit.ly/3m9PrsI' },
		{ id: Math.random(), name: 'Craig', username: 'siliconeidolon', image: 'https://bit.ly/3m9PrsI' },
		{ id: Math.random(), name: 'Ben', username: 'benisphere', image: 'https://bit.ly/3m9PrsI' },
	]

	const initialFormState = { id: null, name: '', username: '', image: '' }

	// Setting state
	const [ users, setUsers ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)
	const [ editing, setEditing ] = useState(false)

	// CRUD operations
	const addUser = user => {
		user.id = Math.random()
		setUsers([ ...users, user ])
	}

	const deleteUser = id => {
		setEditing(false)

		setUsers(users.filter(user => user.id !== id))
	}

	const updateUser = (id, updatedUser) => {
		setEditing(false)

		setUsers(users.map(user => (user.id === id ? updatedUser : user)))
	}

	const editRow = user => {
		setEditing(true)

		setCurrentUser({ id: user.id, name: user.name, username: user.username, image: user.image })
	}

	return (
		<div className="container">
			<h1>CRUD App with Hooks</h1>
			<div className="flex-row">
                
				<Link to="/Users"><button>Users List</button></Link>
                <Link to="/Adduser"><button>Add user</button></Link>
                <Switch>
                    <Route exact path="/Users" >
                        {users.map((user, i) => (
                        <UserCard key={i} user={user} editRow={editRow} deleteUser={deleteUser}/>))}
                     </Route>

                    <Route path="(/Adduser|Edituser)"    >
                           {editing ? (
							<AddUserForm 
							addUser={addUser}
							editing={editing}
                            />) : (
								<AddUserForm 
								addUser={addUser}
								editing={editing}
								setEditing={setEditing}
								currentUser={currentUser}
								updateUser={updateUser} />
							)
						}
					</Route>
                </Switch>
			</div>
		</div>
	)
}

export default App
