import React, { useState, Fragment } from 'react'
import AddUserForm from './forms/AddUserForm'
import UserCard from './tables/UserCard'
import {Switch, Route, Link} from 'react-router-dom'



const App = () => {
	// Data
	const usersData = [
		{ id: Math.random(), name: 'Meftah Jaâfer', username: 'fmeftah_jaafer', image: 'https://bit.ly/3m9PrsI' },
		{ id: Math.random(), name: 'Elias Ben Azouz', username: 'elias_benazzouz', image: 'https://bit.ly/3m9PrsI' },
	]

	const initialFormState = { id: null, name: '', username: '', image: '' }

	// Setting state
	const [ users, setUsers ] = useState(usersData)
	const [ currentUser, setCurrentUser ] = useState(initialFormState)
	const [ editing, setEditing ] = useState(false)

	// CRUD operations
	const addUser = user => {
		user.id = Math.random()
		setUsers([ ...users, user ])
	}

	const deleteUser = id => {
		setEditing(false)
		setUsers(users.filter(user => user.id !== id))
	}

	const updateUser = (id, updatedUser) => {
		setEditing(false)
		setUsers(users.map(user => (user.id === id ? updatedUser : user)))
	}

	const editRow = user => {
		setEditing(true)
		setCurrentUser({ id: user.id, name: user.name, username: user.username, image: user.image })
    }
    


	return (
		<div className="container">
			<h1>CRUD App with Hooks</h1>
			<div className="flex-row">
          
			{users.map((user, i) => (
        <UserCard key={i} user={user} editRow={editRow} deleteUser={deleteUser}/>))}	
                
			</div>
		</div>
	)
}

export default App
